import React, { useState } from 'react'
import { Feather } from '@expo/vector-icons'
import { LinearGradient } from 'expo-linear-gradient'
import { View, TextInput, StyleSheet, KeyboardType, TouchableOpacity, Keyboard } from 'react-native'
import maskHex from '../../utils/mask';
import Colors from '../../constants/Ð¡olors'
import BottomSheetComponent from "../BottomSheet";
interface TextInputProps {
    type?: 'text' | 'email' | 'password' | 'hex'    
    value?: string
    style?: object
    prefix?: object
    disabled?: boolean
    inputRef?: React.RefObject<any>
    onChange?: any
    maxLength?: number
    autoFocus?: boolean
    multiline?: boolean
    inputStyle?: object
    placeholder?: string
    keyboardType?: KeyboardType
}

const CustomTextInput: React.FunctionComponent<TextInputProps> = (props) => {
    const { type = 'text', style, value, prefix, onChange, maxLength, disabled, inputRef, multiline, inputStyle, placeholder, ...rest } = props

    const [focused, setFocused] = useState(false)
    const [visible, setVisible] = useState(false)
    

    function handleChange(text: string) {
        if (type === "hex") {
          const value = maskHex(text);
          onChange(value);
        } else  onChange(text);
      }

    const handleBlur = () => {
        setFocused(false)
    }

    const handleFocus = () => {
         type == 'hex' && (Keyboard.dismiss(), setFocused(true))
         setFocused(true)
       
    }

    

    const commonProps = {
        value,
        placeholder,
        maxLength,
        onBlur: handleBlur,
        onFocus: handleFocus,
        onChangeText: (value: string) => handleChange(value),

        editable: !disabled,

        style: [styles.input, inputStyle, prefix && styles.inputPrefix],
        selectionColor: Colors.Primary,
        placeholderTextColor: Colors.TextPlaceholder
    }



    return (
        <LinearGradient
            start={{ x: 0.2, y: 0.1 }}
            style={[
                styles.container,
                style,
                multiline && styles.inputArea
            ]}
            colors={focused
                ? ['rgb(242, 83, 56)', 'rgb(250, 131, 15)']
                : [Colors.Border, Colors.Border]
            }
            locations={focused ? [0.08, 0.8] : [1, 1]}
        >
            
            <TextInput
                multiline={multiline}
                keyboardType={'default'}
                
                secureTextEntry={type === 'password' && !visible}
                contextMenuHidden={type === 'password'}
                {...commonProps}
                {...rest}
            />
            
            {type === 'password' &&
                <TouchableOpacity
                    style={styles.eye}
                    onPress={() => setVisible(!visible)}
                >
                    <Feather
                        size={24}
                        name={visible ? 'eye' : 'eye-off'}
                        color={Colors.Primary}
                    />
                </TouchableOpacity>
            }
            {prefix &&
                <View style={styles.prefix}>
                    {prefix}
                </View>
            }
        </LinearGradient>
    )
}

export default CustomTextInput

const styles = StyleSheet.create({
    eye: {
        top: 0,
        right: 0,
        padding: 8,
        position: 'absolute'
    },
    icon: {
        top: 10,
        left: 10,
        zIndex: 1,
        position: 'absolute'
    },
    input: {
        flex: 1,
        color: Colors.TextPrimary,
        padding: 10,
        fontSize: 14,
        borderRadius: 8,
        backgroundColor: '#fff'
    },
    prefix: {
        position: 'absolute',
        paddingVertical: 6.5,
        paddingHorizontal: 10
    },
    inputArea: {
        height: 150
    },
    inputPrefix: {
        paddingLeft: 26
    },
    container: {
        height: 43,
        padding: 2,
        borderRadius: 10
    },
    transparent: {
        flex: 1,
        color: '#fff',
        fontSize: 16,
        paddingBottom: 2
    }
})
