export default function maskHex(value: string) { 
    // ## ## ## ##
    value = value.replace(/[^0-9a-fA-F]/g, "");       
    value = value.replace(/(^[0-9a-fA-F]{2})([0-9a-fA-F])/g, "$1 $2");
    value = value.replace(/([0-9a-fA-F]{2})([0-9a-fA-F]{2})/g, "$1 $2");
    value = value.replace(/([0-9a-fA-F]{2})([0-9a-fA-F]{2})/g, "$1 $2");
    return value;
  }
