import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  TouchableWithoutFeedback,
  Keyboard,Modal
} from "react-native";
import Header from "./components/Header";
import { BottomSheet } from "react-native-elements";
import { SafeAreaView } from "react-native-safe-area-context";
import Input from "./components/Input";
import { useState, useEffect } from "react";
import maskHex from "./utils/mask";
import Button from "./components/Button";

import * as font from "expo-font";
import { HexKeyboard } from "./components/HexKeyboard";

import BottomSheetComponent from "./components/BottomSheet";
import { ScreenHeight } from "react-native-elements/dist/helpers";

export default function App() {
  const [nameIdentifier, setNameIdentifier] = useState("");
  const [numberIdentifier, setNumberIdentifier] = useState("");

  useEffect(() => {
    font.loadAsync({
      OpenSans: require("./assets/fonts/OpenSans.ttf"),
    });
  }, []);

  return (
    <View>
      <StatusBar style="inverted" />
      <Header title="Идентификатор" />
      <View style={styles.container}>
        <Input
          placeholder="Название"
          style={styles.input}
          value={nameIdentifier}
          onChange={(e: string) => setNameIdentifier(e)}
        />
        <Input
          placeholder="Номер"
          type="hex"
          style={styles.input}
          value={numberIdentifier}
          onChangeMask={(e: string)=>setNumberIdentifier(maskHex(e))}
        />
        <Button style={styles.button}>Сохранить</Button>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    marginHorizontal: 16,
  },
  maskInput: {
    padding: 10,
    fontSize: 14,
    borderRadius: 8,
    backgroundColor: "#fff",
  },
  input: {
    marginTop: 16,
  },
  button: {
    marginTop: 13,
  },
  keyboardStyle: {
    flex: 2,
    marginTop: "100%",
    maxHeight: "26%",
  },
});
